var sendPushNotification = function(userId,obj)
{
    var pushQuery = new Parse.Query(Parse.Installation);
    pushQuery.equalTo("owner",userId);
    Parse.Push.send({
        where: pushQuery, // Set our Installation query
        data: {
            alert: "Unfortunately, "+ obj.get("title_event") + " was not confirmed. Try joining another Experience today."
        }
        },{
            success: function() {
                console.log("Successfull push notification");
            },
            error: function(error) {
                console.log("Error push notification");
            }
    });
}

var getEventsForNotConfirmedEmail = function(obj,users)
{
    var todayDate = new Date();
    todayDate.setHours(0,0,0,0);
    var nextDayDate = new Date();
    nextDayDate.setDate(todayDate.getDate()+1);
    nextDayDate.setHours(0,0,0,0);
     
    var nextDate = new Date();
    nextDate.setDate(todayDate.getDate()+7);
    nextDate.setHours(0,0,0,0);
     
    console.log("Next day date : " + nextDayDate);
    console.log("Date after six days : " + nextDate);
     
    var query = new Parse.Query("EventDetail");
    query.limit(500);
    query.lessThan("date", nextDate);
    query.greaterThan("date", nextDayDate);
    query.notEqualTo("type_event","Ladies Only");
    query.notEqualTo("type_event","VIP");
    query.ascending("date");
    console.log("Fetching new events.");
    query.find({
        success : function(events) {
            console.log(events.length + " events fetched.");
            if(events.length > 1)
            {
                for(var j=0; j<users.length; ++j)
                {
                    sendEmailForEventNotConfirmed(users[j],obj,events);
                }
            }                       
        },
        error : function(error) {
            alert("Error: " + error.code + " " + error.message);
        }
    });
}

var sendPushNotificationForNotConfirmedEvent = function(obj)
{
    var relation = obj.relation("attendees");
    var query = relation.query();
    query.find({
        success : function(users) {
            if(users.length > 0)
            {
                console.log("Users found for event : " +  obj.get("title_event"));
                getEventsForNotConfirmedEmail(obj,users);
            }
            for(var j=0; j<users.length; ++j)
            {
                var usr = users[j]
                var userId = usr.id;
                sendPushNotification(userId,obj);
            }
                         
                         
        },
        error : function(error) {
            alert("Error: " + error.code + " " + error.message);
        }
    });
}

