Parse.Cloud.define("reportUser", function(request, response) {
 
    Parse.Cloud.useMasterKey();
    var user = request.user;
    var query = new Parse.Query(Parse.User);
    query.equalTo("objectId", request.params.objectId);
    query.first({
        success: function(anotherUser) {
            var relation = anotherUser.relation("reportedBy");
            relation.add(user);
 
            anotherUser.save(null, {
                success:function(){response.success("Successfully reported.");},
                error:function(error){response.error("Could not add in relation");}
            });
        },
        error: function(error) {
            response.error("Could not add in relation");
        }
  });
});
 
